// kuku.js
// generates two 9x9 quiz.

var Matrix = require("../index.js");
var m = new Matrix();

function rand(){
	return 3 + Math.floor(Math.random() * 7);
}
m.writeString(rand() + "*" + rand() + "?");
m.breakLine();
m.writeString(rand() + "*" + rand() + "?");
m.printBitmap();
module.exports = m.getBitmap();
